def create_image(input):

    tile = 50

    my_uuid = uuid.uuid4().hex
    print(uuid)

    ascii = [ord(c) for c in input]
    width = math.ceil(math.sqrt(len(ascii)))

    print(ascii)
    print(width)

    im = Image.new('P', [width*tile, width*tile])
    im.putpalette(palette)

    draw = ImageDraw.Draw(im)

    for colour in enumerate(ascii):
        # position: color[0]
        # item: color[1]

        row = math.floor(colour[0] / width)

        x1 = (colour[0]*tile)-(tile*(row*width))
        y1 = row*tile+1
        x2 = ((colour[0]+1)*tile)-(tile*(row*width))
        y2 = (row+1)*tile

        if (row == 0):
           y1 = y1 - 1 

        print(f"Pos: {colour[0]}, Value: {colour[1]} \n x1: {x1} y1: {y1} row: {row} \n x2: {x2} y2: {y2} ")

        draw.rectangle(xy=[(x1,y1), (x2,y2)], fill=colour[1] ,outline=None, width=0)
        

    print(im.format, im.size, im.mode)

    #im.show()

    im.save(f"static/images/{my_uuid}.png")

    return f"static/images/{my_uuid}.png"

    #end create_image

if (__name__ == '__main__'):
    create_image("1234567890abcdefghijklmn")

